import React from "react";
import "./CourseItem.css";
import { connect } from "react-redux";
import { addSavedItem } from "../../actions/actions";

const CourseItem =({course, addSavedItem, saved})  => {

  return (
    <div className="course">
      <div className="img__container">
        <img
          className="course__img"
          src={course.courseImage}
          alt={course.title}
        />
      </div>




        <h3 className="course__title m-left">{course.title}</h3>

      <span className="course__author m-left">By: {course.author}</span>
      <div className="course__price">
        <span className="price__number">{course.price}</span>
        <span>$</span>
      </div>

      <span className={course.isHot ? "course__status" : ""}>
        {course.isHot ? "Hot" : null}
      </span>
      <span className="add" onClick={()=> addSavedItem(course)}>
        saved.includes(course)? 
        (<img src="https://icons8.com/icon/42887/bookmark"/>)
        :
        (<img src="https://icons8.com/icon/47701/bookmark"/>)
             </span>
    </div>
  );
};
const getSavedFromStore = state =>{
  return{
    saved: state.savedList.saved,
  }
}

export default connect(getSavedFromStore, {addSavedItem}) (CourseItem);
