import React from "react";
import "./CourseItem.css";

const CourseItem = props => {
  const courses = props.courses;

  return (
    <div className="course">
      <div className="img__container">
        <img
          className="course__img"
          src={courses.courseImage}
          alt={courses.title}
        />
      </div>



      <Link className='course__link' to={`/courses/${courses.id}`}>
        <h3 className="course__title m-left">{courses.title}</h3>
      </Link>
      <span className="course__author m-left">By: {courses.author}</span>
      <div className="course__price">
        <span className="price__number">{courses.price}</span>
        <span>$</span>
      </div>

      <span className={courses.isHot ? "course__status" : ""}>
        {courses.isHot ? "Hot" : null}
      </span>
    </div>
  );
};

export default CourseItem;
